CREATE TABLE IF NOT EXISTS `users_information` (
  `id` int(11) unsigned NOT NULL,
  `user_id` int(11) unsigned NOT NULL,
  `email` varchar(100) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `post` varchar(255) DEFAULT NULL,
  `contact` varchar(255) DEFAULT NULL,
  `content` text,
  `photo` varchar(100) DEFAULT NULL,
  `modified` datetime NOT NULL
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `users_information`
--

INSERT INTO `users_information` (`id`, `user_id`, `email`, `name`, `post`, `contact`, `content`, `photo`, `modified`) VALUES
(1, 1, 'admin.is@nuft.edu.ua', 'Адміністратор ІС', 'Адміністратор сайту кафедри інформаційних систем', '', '', 'img/users/d922b9a3dcb1db560a3bdc0792f0c78f.jpeg', '2016-04-07 00:35:53'),
(2, 2, '', 'Алішов Надір Ісмаїл-огли', 'професор, доктор технічних наук. Лауреат премії Ради Міністрів СРСР і Української молодіжної премії ім. Миколи Островського у галузі науки і техніки. Академік Української академії інформатики', '', '<p>Академік Української академії інформатики. Доктор технічних наук, професор за фахом &laquo;Обчислювальні машини, системи і мережі&raquo;. З 1972 р. працює в Інституті кібернетики ім. В.М. Глушкова НАН України. В даний час є провідним науковим співробітником цього інституту.</p>\r\n\r\n<p>Автор близько 200 наукових праць, зокрема декількох монографій, 10 винаходів і патентів. Впроваджено більше 30-ти апаратно-програмних засобів, комп&#39;ютерних систем і мереж комп&#39;ютерів.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>комп&#39;ютерні мережі і мережеві технології</strong></p>\r\n', NULL, '2016-04-07 01:43:43'),
(3, 3, '', 'Самсонов Валерій Васильович', 'Завідувач кафедри<br>професор, кандидат технічних наук. Нагороджений Почесною грамотою Кабінету Міністрів України, Почесною грамотою Академії педагогічних наук України, Почесною грамотою Центрального комітету профспілок працівників освіти і науки України', '', '', NULL, '2016-04-07 01:45:46'),
(4, 4, '', 'Кургаєв Олександр Пилипович', 'професор, доктор технічних наук. Лауреат державної премії України в галузі науки та техніки', '', '<p>Професор, доктор технічних наук.</p>\r\n\r\n<p>Працює на кафедрі з 2011 року.</p>\r\n\r\n<p>Провідний науковий співробітник Інституту кібернетики імені В.М. Глушкова НАН України. Науковий стаж - більше 37 років.</p>\r\n\r\n<p>У 1968 році закінчив кафедру напівпровідників радіофізичного факультету Київського державного університету ім. Т.Г. Шевченко, кваліфікація радіофізик, за фахом &ndash; напівпровідникова електроніка.&nbsp;</p>\r\n\r\n<p>Автор трьох монографій і близько 180 друкованих робіт, включаючи 77 винаходів і патентів з різних розділів комп&#39;ютерної науки.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>архітектура комп&#39;ютерних систем обробки даних і знань; дослідження комп&#39;ютерних систем обробки знань, представлених у формі наукової теорії.</strong></p>\r\n', NULL, '2016-04-07 01:46:35'),
(5, 5, '', 'Литвинов Валерій Андроникович', 'професор, доктор технічних наук. Лауреат державної премії України (УРСР) в галузі науки та техніки<p>Професор, доктор технічних наук.</p>  <p>Працює на кафедрі з 2011 року.</p>  <p>У 1960 р. закінчив радіотехнічний факультет Київського політехнічного інст', '', '<p>Професор, доктор технічних наук.</p>\r\n\r\n<p>Працює на кафедрі з 2011 року.</p>\r\n\r\n<p>У 1960 р. закінчив радіотехнічний факультет Київського політехнічного інституту за спеціальністю &quot;електронні прилади&quot;.</p>\r\n\r\n<p>Лауреат державної премії України (УРСР) в галузі науки та техніки. Підготував 15 кандидатів технічних наук. Читав курси лекцій у Київському філіалі Московського інституту підвищення кваліфікації, Інституті культури, Національному авіаційному університеті. З 1996 р. працює в Інституті проблем математичних машин та систем НАН України на посаді провідного наукового співробітника відділу &ldquo;Інтелектуальних інформаційно-аналітичних систем&rdquo;.&nbsp;</p>\r\n\r\n<p>У 1960 р. закінчив радіотехнічний факультет Київського політехнічного інституту за спеціальністю &quot;електронні прилади&quot;.&nbsp;</p>\r\n\r\n<p>Автор 170 наукових праць, в тому числі &ndash; 2 монографії, брошур та статей у таких журналах як &ldquo;Автоматика и телемеханика&rdquo;, &ldquo;Техническая кибернетика&rdquo;, &ldquo;Программирование&rdquo;, &ldquo;Кибернетика&rdquo;, &ldquo;Управляющие системы и машины&rdquo;, &ldquo;Математичні машини та системи&raquo;, та ін.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>пошукові системи, системи підтримки прийняття рішень, інтелектуалізований інтерфейс користувача, оцінки рівня інтелектуалізації програмно-технічних систем.</strong></p>\r\n\r\n<p><strong>Викладацька: А-338: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 01:47:20'),
(6, 6, '', 'Савченко Юлій Григорович', 'професор, доктор технічних наук', '', '<p>Професор, доктор технічних наук.</p>\r\n\r\n<p><span 1.7em="" style="\\&quot;line-height:">Закінчив Київський політехнічний інститут у 1960 р. за фахом &laquo;Електронні прилади&raquo;.&nbsp;</span></p>\r\n\r\n<p><span 1.7em="" style="\\&quot;line-height:">Працює в НУХТ з 2000 р., на кафедрі з 2002 р.</span></p>\r\n\r\n<p><span 1.7em="" style="\\&quot;line-height:">Автор більше 150 наукових праць, авторських свідоцтв, навчального посібника.&nbsp;</span></p>\r\n\r\n<p><span 1.7em="" style="\\&quot;line-height:">Науково-педагогічний напрямок &ndash; <strong>методи забезпечення надійності електронної апаратури, технічна діагностика, завадостійке кодування та криптографічний захист інформації.</strong></span></p>\r\n', NULL, '2016-04-07 01:47:47'),
(7, 7, '', 'Сільвестров Антон Миколайович', 'професор, доктор технічних наук', '', '<p>Професор, доктор технічних наук.</p>\r\n\r\n<p>Закінчив Київський політехнічний інститут у 1969 р.&nbsp;</p>\r\n\r\n<p>Працює на кафедрі з 2006 р.</p>\r\n\r\n<p>Під керівництвом проф. Сільвестрова А.М. було розроблено методику високоточного визначення аеродинамічних характеристик літальних апаратів у нелінійних диференційних рівняннях, які описують ці характеристики. В період 1985-1992 років виконано понад 20 бюджетних та госпрозрахункових НДР з цієї тематики, серед них комплекс розробок за міжгалузевою програмою &ldquo;Політ&rdquo; для провідних авіаційних підприємств НВО &ldquo;Молния&rdquo;, АНТК ім. Антонова, ЦНДІ автоматики та гідравліки (м. Москва), К.Б. Мікояна та ін. Об&rsquo;єкти досліджень: космічний літальний апарат &ldquo;Буран&rdquo;, тактичні ракети, літаки М-17, МІГ-27, МІГ-29, АН-72, ТУ-154, ШЛ-18 та ін.</p>\r\n\r\n<p>Автор 8 монографій та 200 наукових статей, доповідей та авторських свідоцтв, підручника &laquo;Електротехніка з основами електроніки&raquo;.</p>\r\n\r\n<p>Підготував 7 кандидатів наук та 2 доктори наук.&nbsp;</p>\r\n\r\n<p><strong>Науково-педагогічний напрямок &ndash; методи ідентифікації, автоматизації і оптимізації складних динамічних систем різної природи і призначення.</strong></p>\r\n', NULL, '2016-04-07 01:48:18'),
(8, 8, '', 'Маноха Людмила Юріївна', 'доцент, кандидат технічних наук, декан факультету автоматизації та комп’ютерних систем', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Декан факультету Автоматизації та комп&rsquo;ютерних систем.<br />\r\nЗакінчила Київський національний університет ім. Тараса Шевченко за спеціальністю &laquo;Прикладна математика&raquo; у 1983 році.<br />\r\nКандидатську дисертацію захистила у 1995 році.&nbsp;<br />\r\nПрацює в НУХТ з 1986 р., на кафедрі з 2002 р.</p>\r\n\r\n<p>Викладає блок лекційних курсів, веде лабораторні роботи та керує курсовими роботами з базової дисципліни &laquo;Комп&rsquo;ютерна техніка та програмування&raquo; для студентів напряму &laquo;Автоматизація та комп&rsquo;ютерно-інтегровані технології&raquo;; &laquo;Сучасна теорія управління&raquo; для студентів напряму &laquo;Комп&rsquo;ютерні науки&raquo;. Для цих дисциплін створила методичне забезпечення: навчальні програми курсів, конспекти лекцій в електронному вигляді, методичні вказівки, модульні контрольні заходи. Керує науково-дослідною роботою студентів, здійснює керівництво дипломними та магістерськими роботами.&nbsp;</p>\r\n\r\n<p>Автор 49 публікацій, з них 19 наукового та 30 навчально-методичного характеру.</p>\r\n\r\n<p><span 1.7em="" style="\\&quot;line-height:">Науково-педагогічний напрямок &ndash; <strong>створення компонент методичного, математичного та інформаційного забезпечення автоматизованих систем управління підприємств харчової та переробної промисловості, розробка електронних засобів навчання.</strong></span></p>\r\n', NULL, '2016-04-07 01:48:46'),
(9, 9, '', 'М’якшило Олена Михайлівна', 'доцент, кандидат технічних наук, заступник завідувача кафедри', '', '<p>Закінчила у 1974 році Київський політехнічний інститут, факультет АЕПБ за спеціальністю &laquo;Обчислювальна техніка&raquo;. Працювала у Київському інституті автоматики, приймала участь у розробці автоматизованих систем управління промисловими підприємствами, в тому числі для цукрових заводів.</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 1991 р.</p>\r\n\r\n<p>Працює в НУХТ із 1998 р., на кафедрі з 2002 р.&nbsp;</p>\r\n\r\n<p>Виконує обов&#39;язки заступника завідувача кафедрою, відповідає за складання навчальних планів, а також за організацію захисту магістерських робіт. Керує виконанням дипломних та магістерських робіт. Підготувала одного кандидата технічних наук. Керує науковою роботою двох аспірантів. Викладає дисципліни: &quot;Організація баз даних та знань&quot;, &quot;Проектування інформаційних систем&quot;, &quot;Спіральна методологія розробки систем&quot;.</p>\r\n\r\n<p>Автор більше 40 наукових праць, в тому числі: навчальних посібників, &nbsp;конспектів лекцій та більше двадцяти методичних розробок за наведеними дисциплінами.&nbsp;</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>моделювання, розроблення та аналіз баз даних, OLAP-технології, CASE- технології, сучасні технології розроблення систем.</strong></p>\r\n\r\n<p><strong>Викладацька: А-338: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 01:50:07'),
(10, 10, '', 'Чаплінський Юрій Петрович', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Закінчив у 1982 р. факультет кібернетики Київського національного університету ім. Тараса Шевченко.</p>\r\n\r\n<p>Кандидатську дисертацію захистив у 1988 році.&nbsp;</p>\r\n\r\n<p>Працює на кафедрі з 2007 р.</p>\r\n\r\n<p>Приймав участь та керував виконанням спільних проектів з українськими та закордонними фахівцями. Проходив стажування в університетах та компаніях США, Бельгії, Великобританії та Італії. Брав участь в роботі міжнародних конференцій та виставках в Нідерландах, США, Туреччині, Єгипті та Індії. Деякий час був національним експертом з інформаційних технологій проекту ООН.</p>\r\n\r\n<p>Автор 57 наукових та науково-методичних праць.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>розробка математичних методів і алгоритмів прийняття рішень, що базуються на технології системної оптимізації, проведення системного аналізу та розробки алгоритмів прийняття рішень в багаторівневих ієрархічних системах, розробка прикладних знаннєво-орієнтованих технологій та систем підтримки прийняття рішень.</strong></p>\r\n\r\n<p><strong>Викладацька: А-340: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 01:51:55'),
(11, 11, '', 'Горлова Тетяна Михайлівна', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Закінчила Київський політехнічний інститут у 1972 р.&nbsp;</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 1984 році.</p>\r\n\r\n<p>На кафедрі працює з 2004 року.</p>\r\n\r\n<p>Автор більше 40 друкованих праць, з них одна монографія і один навчальний посібник, п&rsquo;ять методичних вказівок з дисциплін, що викладаються.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>технічна кібернетика і теорія інформації.</strong></p>\r\n\r\n<p><strong>Викладацька: А-340: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 01:52:30'),
(12, 12, '', 'Загоровська Лариса Григорівна', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Закінчила Київський національний університет ім. Тараса Шевченко у 1977 році.</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 2000 році.&nbsp;</p>\r\n\r\n<p>Працює на кафедрі з 2002 р.</p>\r\n\r\n<p>Автор 2 посібників та більше 70 методичних вказівок і наукових праць, опублікованих у фахових виданнях.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>дослідження й системний аналіз діяльності підприємств, інжиніринг та автоматизація бізнес-процесів.</strong></p>\r\n\r\n<p><strong>Викладацька: А-338: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 01:53:26'),
(13, 13, '', 'Поворознюк Назар Іванович', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Закінчив Київський політехнічний інститут у 1978 р.&nbsp;</p>\r\n\r\n<p>Працює на кафедрі з 2003 р.</p>\r\n\r\n<p>Автор 2 підручників, 3 посібників, 1 електронного засобу навчання та більше 90 праць, опублікованих у фахових наукових виданнях.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>теорія інформації та обробки сигналів.</strong></p>\r\n\r\n<p><strong>Викладацька: А-333: (044) 287-92-46</strong></p>\r\n', NULL, '2016-04-07 01:54:01'),
(14, 14, '', 'Андріюк Олена Петрівна', 'доцент, кандидат фізико-математичних наук', '', '<p>Доцент, кандидат фізико-математичних наук.</p>\r\n\r\n<p>Закінчила Київський національний університет ім. Тараса Шевченко у 1998 році.</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 2007 році.</p>\r\n\r\n<p>Опубліковано 20 друкованих праць.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash;&nbsp;<strong>теорія функцій комплексної змінної.</strong></p>\r\n', NULL, '2016-04-07 01:54:57'),
(15, 15, '', 'Хлобистова Ольга Андріївна', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Закінчила Київський політехнічний інститут по спеціальності &bdquo;Обчислювальна техніка&rdquo;, Національний університет ім. Тараса Шевченка по спеціальності &bdquo;Теорія ймовірностей і математична статистика&rdquo;. Також має спеціальність &bdquo;бухгалтер-економіст&rdquo;.&nbsp;</p>\r\n\r\n<p>Працює в НУХТ з 2000р., на кафедрі з 2002 р.</p>\r\n\r\n<p>Брала участь в проектуванні автоматизованих систем для хімічного виробництва (Чернігівський і Курський комбінати синтетичних волокон), металургійного виробництва (Магнітогорський металургійний комбінат), для керування газотранспортними системами, атомними електростанціями (Чехія - Моховце і Куба - Хурагуа). Інший напрямок діяльності - створення державних і галузевих стандартів, яких на її рахунку вже 12. Була науковим керівником розробки серії ДСТУ &bdquo;Безпечність промислових підприємств&rdquo;. На громадських засадах виконує обов`язки заступника керівника Органу з сертифікації автоматизованих систем і програмно-технічних комплексів (ОС СЕПРО АС (ПТК) при Держспоживстандарті України.</p>\r\n\r\n<p>Автор більше 70 наукових праць, має три авторських свідоцтва про винаходи.</p>\r\n\r\n<p>Науково-педагогічний напрямок - <strong>методологічні основи створення інформаційних систем для автоматизованої переробки інформації й управління документообігом.</strong></p>\r\n\r\n<p><strong>Викладацька: А-338: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 01:56:10'),
(16, 16, '', 'Гречко Анастасія Валеріївна', 'доцент, кандидат фізико-математичних наук', '', '<p>Доцент, кандидат фізико-математичних наук, науковий співробітник Інституту кібернетики ім. В.М. Глушкова НАН України.</p>\r\n\r\n<p>Закінчила Національний технічний університет України &quot;Київський політехнічний інститут&quot; у 2001 році.</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 2006 році.</p>\r\n\r\n<p>Опубліковано 7 наукових статей</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>математичні моделі підтримки електронного документообігу, розробка користувацького інтерфейсу розподілених систем з застосуванням Web-технологій.</strong></p>\r\n\r\n<p><strong>Викладацька: А-333: (044) 287-92-46</strong></p>\r\n', NULL, '2016-04-07 01:57:00'),
(17, 17, '', 'Кіктєв Микола Олександрович', 'доцент', '', '', NULL, '2016-04-07 01:57:21'),
(18, 18, '', 'Марченко Віталій Анатолійович', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук, старший науковий співробітник Інституту кібернетики ім. В.М. Глушкова НАН України.</p>\r\n\r\n<p>Закінчив &quot;Національний авіаційний університет&quot; в 2005 р.&nbsp;</p>\r\n\r\n<p>Кандидатську дисертацію захистив у 2008 р. за фахом &quot;Комп&#39;ютерні системи і компоненти&quot;, тема &quot;Засоби інтеграції компонентів захисту мережевого периметру&quot;.</p>\r\n\r\n<p>Працює на кафедрі з 2010 р.</p>\r\n\r\n<p>Автор близько 25 друкованих праць.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>розподілені мережеві технології з підвищеним рівнем захисту оброблюваних даних, новітні методи організації мережевого обміну у розподілених телекомунікаційних системах з високим навантаженням.</strong></p>\r\n\r\n<p><strong>Викладацька: А-333: (044) 287-92-46</strong></p>\r\n', NULL, '2016-04-07 01:57:49'),
(19, 19, '', 'Ночвай Володимир Іванович', 'доцент, кандидат технічних наук', '', '<p>Доцент , кандидат технічних наук.</p>\r\n\r\n<p>Закінчив Національний університет &laquo;Києво-Могилянська академія&raquo; у 2001 році.</p>\r\n\r\n<p>Кандидатську дисертацію захистив у 2009 році.</p>\r\n\r\n<p>Опубліковано близько 30 наукових праць 10 навчально-методичних розробок.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>моделювання забруднення повітря, методи оптимізації, прийняття рішень в умовах невизначеності, інформаційні системи для управління якістю навколишнього середовища</strong></p>\r\n', NULL, '2016-04-07 01:58:20'),
(20, 20, '', 'Грибков Сергій Віталійович', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук. Закінчив НУХТ у 2003 році.&nbsp;</p>\r\n\r\n<p>Працює на кафедрі з 2003 року.</p>\r\n\r\n<p>Кандидатську дисертацію захистив у 2013 році.</p>\r\n\r\n<p>Автор 25 друкованих праць.&nbsp;</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>розробка систем підтримки прийняття рішень в харчовій галузі.</strong></p>\r\n\r\n<p><strong>Викладацька: А-338: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 02:00:01'),
(21, 21, '', 'Харкянен Олена Валеріївна', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Заступник декана заочного факультету АКС.</p>\r\n\r\n<p>Закінчила НУХТ в 1999 р.</p>\r\n\r\n<p>Працює на кафедрі з 2002 року.</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 2013 році.</p>\r\n\r\n<p>Автор понад 35 публікацій. В тому числі: статей у наукових фахових виданнях, методичних вказівок, тез та матеріалів міжнародних та українських наукових конференцій.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>моделювання, розробка та аналіз баз даних, багатовимірний та інтелектуальний аналіз даних, CASE-технології.</strong></p>\r\n\r\n<p><strong>Викладацька: А-338: (044) 287-96-77</strong></p>\r\n', NULL, '2016-04-07 02:01:00'),
(22, 22, '', 'Мошенський Андрій Олександрович', 'доцент, кандидат технічних наук', '', '<p>Доцент, кандидат технічних наук.</p>\r\n\r\n<p>Закінчив у 2005 р. магістратуру НТУУ &laquo;Київський політехнічний інститут&raquo;.</p>\r\n\r\n<p>Працює на кафедрі з 2005 року.</p>\r\n\r\n<p>Кандидатську дисертацію захистив у 2013 році./p&gt;</p>\r\n\r\n<p>Автор 14 публікацій.&nbsp;</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>телекомунікаційні мережі та передача даних.</strong></p>\r\n\r\n<p><strong>Викладацька: А-333: (044) 287-92-46</strong></p>\r\n', NULL, '2016-04-07 02:02:01'),
(23, 23, '', 'Бойко Регіна Олегівна', 'асистент, кандидат технічних наук', '', '<p>Закінчила НУХТ в 2010 році.</p>\r\n\r\n<p>Працює на кафедрі з 2010 р.</p>\r\n\r\n<p>Кандидатську дисертацію захистила у 2014 році.</p>\r\n\r\n<p>Автор 12 публікацій.</p>\r\n\r\n<p>Науково-педагогічний напрямок &ndash; <strong>Інформаційні технології управління в умовах невизначеності.</strong></p>\r\n\r\n<p><strong>Викладацька: А-333: (044) 287-92-46</strong></p>\r\n', NULL, '2016-04-07 02:02:31'),
(24, 24, '', 'Гладка Мирослава Вікторівна', 'старший викладач', 'Викладацька: А-338: (044) 287-96-77', '<p>Закінчила НУХТ в 2003 році.</p>\r\n\r\n<p>Працює на кафедрі з 2004 р.</p>\r\n\r\n<p>Куратор групи четвертого курсу.</p>\r\n\r\n<p>Впроваджувала систему електронного документообігу OPTiMA-WorkFlow на підприємствах України. Працювала бізнес-аналітиком у &quot;Сітронікс інформаційні технології&quot; (&quot;Квазар-Мікро Техно&quot;). Аналітик-тестувальник у компанії &quot;Ітнерготель&quot; - впровадження систем управління HoReCa.</p>\r\n\r\n<p><span 1.7em="" style="\\&quot;line-height:">Науково-педагогічний напрямок &ndash; </span><strong 1.7em="" style="\\&quot;line-height:">дослідження й системний аналіз діяльності підприємств, інжиніринг та автоматизація бізнес-процесів.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Публікації</h2>\r\n\r\n<ol>\r\n	<li>Дослідження та розробка системи підтримки прийняття рішень для удосконалення виробничої діяльності машинобудівного підприємства на базі технології CRM i MRP II, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 70 Наукова конференція молодих вчених, аспірантів і студентів, К.: НУХТ, 2004р. &ndash; Ч.2., С.119-120</li>\r\n	<li>Дослідження та розробка механізму керування інформаційними потоковими процесами відділу маркетингу , [Текст],&nbsp; Гладка М.В., Хлобистова О.А., Наукові праці НУХТ. Додаток до журналу №15, К., НУХТ &ndash; 2004. &ndash; С. 87</li>\r\n	<li>Автоматизація керування потоками робіт і організація конфіденційного документообігу у великих і середніх організаціях , [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 11 Всеукраїнська наукова конференція. Сучасні проблеми прикладної математики та інформатики. Секція: проблеми прикладної та теоретичної інформатики, К., НУХТ - 2004р. &ndash; С125</li>\r\n	<li>Застосування технології WorkFlow при впровадженні стандарту ISO 9000 при автоматизації великих та середніх підприємств. [Текст],&nbsp; Гладка М.В., Редькіна М.О., Міжнародна науково-практична конференція &quot;Наукові дослідження теорія та експеримент&quot;, Полтава - 2005 р., Том 6, С 12 -14</li>\r\n	<li>Особливості автоматизації потоків робіт на ВАТ &laquo;Київський маргариновий завод&raquo; , [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 71 Наукова конференція молодих вчених, аспірантів і студентів, К.: НУХТ, 2005р - Ч.2., С.- 119</li>\r\n	<li>Реалізація концепції ISO на базі технології WorkFlow, [Текст],&nbsp; Гладка М.В., Редькіна М.О., Журнал ComputerWorld/ Україна. К.,2005. №27(508), С. 25</li>\r\n	<li>WorkFlow- проводник изменений [Текст],&nbsp; Гладка М.В., Василенко А.В., Журнал &laquo;IT-Менеджер&raquo; №6(12), К., 2005. С 9-13</li>\r\n	<li>Система управления потоками работ OPTiMA-WorkFlow [Текст],&nbsp; Гладка М., Ткаченко А., Журнал &laquo;Секретарь-референт&raquo;, № 05(30),К., 2005, С. 68-70</li>\r\n	<li>Реінжиніринг бізнес-процесів на основі сучасних інформаційних технологій , [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 72 Наукова конференція молодих вчених, аспірантів і студентів,К.: НУХТ, 2006р. &ndash;С.126-127</li>\r\n	<li>Вибір методологій автоматизації технологічного об&rsquo;єкту, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., Автоматизація виробництва, 2006р. - С.</li>\r\n	<li>Оцінка і вибір CASE-засобів для опису бізнес-процесів організації, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 73 Наукова конференція молодих вчених, аспірантів і студентів,&nbsp; К.:&nbsp; НУХТ, 2007р., Ч.2., С.-140</li>\r\n	<li>Технологія вибору CASE-засобу на базі типового рішення, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 74 наукова конференція молодих учених, аспірантів і студентів, К.:&nbsp; НУХТ &ndash; 2008 &ndash; С. 372</li>\r\n	<li>Використання багатофакторної моделі в системі планування харчового виробництва, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 78 міжнародна наукова конференція молодих учених, аспірантів і студентів, К.:&nbsp; НУХТ &ndash; 2012 &ndash; С.333-334</li>\r\n	<li>Особливості прогнозування попиту методом ковзного середнього на харчових підприємствах, [Текст],&nbsp; Гладка М.В., Камінсь-кий Г.А., 78 міжнародна наукова конференція молодих учених, аспірантів і студентів, К.:&nbsp; НУХТ &ndash; 2012 &ndash; С.337</li>\r\n	<li>Застосування стандартів європейського союзу до державних служб, [Текст],&nbsp; Гладка М.В., Макушева К., 78 міжнародна наукова конференція молодих учених, аспірантів і студентів, К.:&nbsp; НУХТ &ndash; 2012 &ndash; С.337-338</li>\r\n	<li>Розробка автоматизованої системи контролю безпечності м&rsquo;ясопереробного підприємства, [Текст],&nbsp; Гладка М.В., Фоменко І., 78 міжнародна наукова конференція молодих учених, аспірантів і студентів, К.:&nbsp; НУХТ &ndash; 2012 &ndash; С.340</li>\r\n	<li>Процесно-орієнтоване управління якістю харчових виробів, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., ХІХ міжнародна конференція з автоматичного управління, Автоматика/ Automatics -2012, &laquo;Проблеми управління та інформатики&raquo;, К., НУХТ &ndash; 2012</li>\r\n	<li>Процесно-орієнтоване управління якістю харчових виробів, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., UKRAINIAN FOOD JOURNAL № 3, Kиїв 2012, с 78-82</li>\r\n	<li>Інформаційно-обчислювальні комплекси та АСУ [Електронний ресурс] : методичні рекомендації до виконання лабораторних робіт для студентів напряму підготовки 6.050101 &quot;Комп&#39;ютерні науки&quot; усіх форм навч. / уклад. О. А. Хлобистова, М. В. Гладка, К. Є. Бобрівник. - К. : НУХТ, 2012. &ndash; 83 с.&nbsp;</li>\r\n	<li>Гнучкі методології розроблення програмних продуктів і їх використання в проектуванні інформаційних систем, [Текст],&nbsp; Гладка М.В., Хлобистова О.А., 79 міжнародна наукова конференція молодих учених, аспірантів і студентів &laquo;Наукові здобутки молоді &mdash; вирішенню проблем харчування людства у ХХІ столітті&raquo;, &mdash; К.: НУХТ, 2013 р. &mdash; Ч. 2., с. 608-610</li>\r\n	<li>Управління проектуванням життєвого циклу програмних продуктів з допомогою PDM технологій, [Текст],&nbsp; Гладка М.В., Майстренко А.С., 79 міжнародна наукова конференція моло